import React, {Component} from 'react';

class App extends Component{

  constructor (props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }

  componentDidMount(){
    fetch('https://reqres.in/api/users/1')
      .then(res => res.json())
      .then(json =>{
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  render(){

    var { isLoaded, items } = this.state;

    if (!isLoaded){
      return <div>Loading...</div>;
    }
    else {
      return(
        <div className="App">
          <ul>
            {items.map(item => (
              <li key={item.id}>
                Name: {item.id.first_name} | Email: {item.email}
              </li>
            ))}
          </ul>

        </div>
      ); 
    }
  }
}

export default App;
